	(10691, 10154, 10004, 10691, 'Administrador', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Administrador</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol que permite administrar WeCorp</Description></root>', 1, ''),
	(10692, 10154, 10004, 10692, 'Administrador de cursos', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Administrador de cursos</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol que permite realizar la administración de cursos.\nEste perfil que permite crear cursos con todas sus funcionalidades y usuarios</Description></root>', 1, ''),
	(10693, 10154, 10004, 10693, 'commCreator', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Creador de comunidades</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol que permite la creación de comunidades en la plataforma</Description></root>', 2, ''),
	(10694, 10154, 10004, 10694, 'commEditor', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Editor de las comunidades</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol que permite editar de las comunidades</Description></root>', 2, ''),
	(10695, 10154, 10004, 10695, 'commMember', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Miembro de la comunidad</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES,pt_BR," default-locale="es_ES"><Description language-id="es_ES">Rol que indica que eres miembro de la comunidad</Description><Description language-id="pt_BR">Indica de que o usuário é membro da comunidade.</Description></root>', 2, ''),
	(10696, 10154, 10004, 10696, 'commMentor', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Mentor de la comunidad</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol mentor de la comunidad</Description></root>', 2, ''),
	(10697, 10154, 10004, 10697, 'comowner', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Administrador de la comunidad</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol del administrador de la comunidad</Description></root>', 2, ''),
	(10698, 10154, 10004, 10698, 'Content Add', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Content Add</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Es un perfil similar al perfil Contenidos pero se restringe la edición a sus propios contenidos no permitiendo editar el resto de los contenidos. Esto permite a los Publicadores incluir contenidos en la mediateca que puedan ser utilizados posteriormente en los cursos.</Description></root>', 2, ''),
	(10699, 10154, 10004, 10699, 'Content Manager', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Content Manager</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Gestiona los contenidos del repositorio documental</Description></root>', 2, ''),
	(10701, 10154, 10004, 10701, 'courseEditor', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Editor de curso</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol con permisos de creación de módulos y actividades.\nPuede editar los contenidos del curso y diseñar la acción formativa y corregir las actividades de los alumnos.</Description></root>', 2, ''),
	(10702, 10154, 10004, 10702, 'courseTeacher', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Tutor</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol tutor de los cursos, puede impartir un curso pero no modificarlo.\nEste rol permite comunicarse con los alumnos a través de Foros, Blog. \nEs un perfil asociado a la dinamización del curso.</Description></root>', 2, ''),
	(10704, 10154, 10004, 10704, 'News Admin', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Title language-id="es_ES">Administrador noticias</Title></root>', '<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales="es_ES" default-locale="es_ES"><Description language-id="es_ES">Rol que administra el blog de noticias</Description></root>', 2, ''),
